import 'package:flutter/material.dart';

class ListGridview extends StatelessWidget{
  ListGridview ({super.key});

  @override
  Widget build(BuildContext context){
    return Scaffold(
      appBar: AppBar(title: Text("List gridview"),backgroundColor: Colors.greenAccent,
      ),
      body: Padding(
        padding: const EdgeInsets.all(8.0),
        child: GridView.builder(
          gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
            crossAxisCount: 2,crossAxisSpacing: 10,mainAxisSpacing: 15
          ),
          itemCount:24 ,
          itemBuilder: (context, index) {
            return Container(
              height: 200,
              width: 150,
              decoration: BoxDecoration(
                borderRadius: BorderRadius.circular(15),
                image: DecorationImage(image:NetworkImage("data:image/jpeg;base64,"),fit: BoxFit.cover)
              ),
            );
          },
        ),
      ),
    );
  }
}